mapper.select:
select goods_no,goods_title,goods_price,goods_img
from goods

  and goods_no = #{goodsNo}
  and goods_title = #{goodsTitle}
  and goods_price = #{goodsPrice}
  and goods_img = #{goodsImg}

mapper.insert:
insert into goods (goods_id, shop_cart_id, c_id, 
  goods_name, goods_title, goods_no, 
  goods_img, goods_price, goods_descriptive, 
  goods_status, add_time, specification
  )
values (#{goodsId,jdbcType=INTEGER}, #{shopCartId,jdbcType=VARCHAR}, #{cId,jdbcType=INTEGER}, 
  #{goodsName,jdbcType=VARCHAR}, #{goodsTitle,jdbcType=VARCHAR}, #{goodsNo,jdbcType=VARCHAR}, 
  #{goodsImg,jdbcType=VARCHAR}, #{goodsPrice,jdbcType=INTEGER}, #{goodsDescriptive,jdbcType=VARCHAR},
  #{goodsStatus,jdbcType=VARCHAR}, #{addTime,jdbcType=TIMESTAMP}, #{specification,jdbcType=VARCHAR}
  )
